import React, { useEffect, useState } from 'react';
import {Line} from 'react-chartjs-2';
import './LargeBox.css';

function LargeBox() {
    const [chartData, setChartData] = useState({})
    const [currentTime, setCurrentTime] = useState('Hour');
    const [currentCoin, setCurrentCoin] = useState('btc')

    function selectTime(e){
        setCurrentTime(e.target.innerText);
    }

    function selectCoin(e){
        // setCurrentCoin(e.);
        console.log(e.target.option);   
    }

    const chart = () => {
        setChartData({
            labels: [36,36,36,37,38,38,39,41,41,42,43,43,43,45,46,46,47,48,48,49,49,50,52,53,54,54,55,55,56,56,56,56,57,57,57,57,57,59,59,59,60,63,63,64,64,65,66,66,67,67,67,68,68,68,70,70,71,72,72,72,73,74,75,76,76,77,78,78,79,80,80,80,82,82,82,83,85,85,86,86,88,88,88,90,90,92,92,92,92,94,94,94,95,96,96,96,97,97,98,98,98,98,99,99,100,26,27,27,27,30,31,31,32,32,32,32,33,33,35,35,36,36,36,37,38,38,39,41,41,42,43,43,43,45,46,46,47,48,48,49,49,50,52,53,54,54,55,55,56,56,56,56,57,57,57,57,57,59,59,59,60,63,63,64,64,65,66,66,67,67,67,68,68,68,70,70,71,72,72,72,73,74,75,76,76,77,78,78,79,80,80,80,82,82,82,83,85,85,86,86,88,88,88,90,90,92,92,92,92,94,94,94,95,96,96,96,97,97,98,98,98,98,99,99,100
            ],
            datasets: [
                {
                    label: '$',
                    data: [36,36,36,37,38,38,39,41,41,42,43,43,43,45,46,46,47,48,48,49,49,50,52,53,54,54,55,55,56,56,56,56,57,57,57,57,57,59,59,59,60,63,63,64,64,65,66,66,67,67,67,68,68,68,70,70,71,72,72,72,73,74,75,76,76,77,78,78,79,80,80,80,82,82,82,83,85,85,86,86,88,88,88,90,90,92,92,92,92,94,94,94,95,96,96,96,97,97,98,98,98,98,99,99,100,26,27,27,27,30,31,31,32,32,32,32,33,33,35,35,36,36,36,37,38,38,39,41,41,42,43,43,43,45,46,46,47,48,48,49,49,50,52,53,54,54,55,55,56,56,56,56,57,57,57,57,57,59,59,59,60,63,63,64,64,65,66,66,67,67,67,68,68,68,70,70,71,72,72,72,73,74,75,76,76,77,78,78,79,80,80,80,82,82,82,83,85,85,86,86,88,88,88,90,90,92,92,92,92,94,94,94,95,96,96,96,97,97,98,98,98,98,99,99,100],
                    backgroundColor: ['rgba(0,0,0,0.09)'],
                    borderColor: 'rgba(247,147,26,1)',
                    borderWidth: 4,
                    borderJoinStyle: 'round',
                    borderCapStyle: 'round',
                    borderWidth: 3,
                    pointRadius: 0,
                    pointHitRadius: 10,
                    lineTension: .2,
                }
            ]
        })
    }

    useEffect(() => {
        chart()
    }, [])
    return (
        <div className="largeBox">
            <div className="largeBox_info">
                <select id="options" onChange={selectCoin}>
                    <option value="btc" >
                        Bitcoin
                    </option>
                    <option value="xzc">
                        ZCoin
                    </option>
                    <option value="trc">
                        Terracoin
                    </option>
                    <option value="etc">
                        Ethereum
                    </option>
                </select>
                <h2>Chart</h2>
            </div>

            <div className="largeBox_selectors">
                <ul>
                    <li className={currentTime === 'Hour' ? "active" : ""} onClick={selectTime}>Hour</li>
                    <li className={currentTime === 'Day' ? "active" : ""} onClick={selectTime}>Day</li>
                    <li className={currentTime === 'Week' ? "active" : ""} onClick={selectTime}>Week</li>
                    <li className={currentTime === 'Month' ? "active" : ""} onClick={selectTime}>Month</li>
                    <li className={currentTime === 'Year' ? "active" : ""} onClick={selectTime}>Year</li>
                </ul>
            </div>

            <div className="largeBox_graph">
                <Line data={chartData} options={{
                    responsive: true,
                    maintainAspectRatio: true,
                    title: {text: 'ThickBoyz', display: false},
                    legend: {display: false},
                    layout: {
                        padding: {
                          left: 0,
                          right: 0,
                          top: 0,
                          bottom: 0
                        }
                      },
                      scales: {
                        xAxes: [{
                          display: false,
                          gridLines: {}
                        }],
                        yAxes: [{
                          display: false,
                          gridLines: {}
                        }]
                      },
                      tooltips: {
                        callbacks: {
                          //This removes the tooltip title
                          title: function() {}
                       },
                        //this removes legend color
                        displayColors: false,
                        yPadding: 10,
                        xPadding: 10,
                        position: 'nearest',
                        caretSize: 10,
                        backgroundColor: 'rgba(255,255,255,.9)',
                        bodyFontSize: 15,
                        bodyFontColor: '#303030' 
                      }
                    }}/>   
            </div> 
        </div>
    )
}
export default LargeBox
