import React, {useState, useEffect} from 'react';
import Header from './Header';
import SideBar from './Sidebar';
import SmallBox from './SmallBox';
import LongBox from './LongBox';
import HorzBox from './HorzBox';
import LargeBox from './LargeBox';
import AssetsBox from './AssetsBox';
import './App.css';

function App() {
  const [base, setBase] = useState('BTC');

  useEffect(() => {
    fetch(`https://rest.coinapi.io/v1/exchangerate/${base}?apikey=23713BE8-C167-440A-BB2B-4F934429B382`)
    .then(response => response.json())
    .then(data => console.log(data));
  }, []);



  return (
    <div className="app">
      {/* Header */}
      <Header />

      {/* Left SideBar */}
      <SideBar />

      {/* SmallBox - Balance */}
      <SmallBox className="balance"/>

      {/* SmallBox - Bitcoin */}
      <SmallBox className="bitcoin"/>

      {/* SmallBox - Terracoin */}
      <SmallBox className="terracoin"/>

      {/* SmallBox - ZCoin */}
      <SmallBox className="zcoin"/>

      {/* SmallBox - Ethereum */}
      <SmallBox className="ethereum"/>

      {/* LongBox - Recent Operations */}
      <LongBox />

      {/* HorizontalBox - Buy/Sell Coins */}
      <HorzBox />

      {/* LargeBox - Graph (BTC) */}
      <LargeBox />

      {/* AssetBox - Assets*/}
      <AssetsBox />
    </div>
  );
}

export default App;
