import React, { useEffect, useRef, useState } from 'react';
import {Line} from 'react-chartjs-2';
import btc from './images/BC_Logo_.png';
import './SmallBox.css';

function SmallBox(props) {
    const [chartData, setChartData] = useState({})

    // let gradient = chart.createLinearGradient(0, 0, 0, 400);

    // gradient.addColorStop(0, 'rgba(247,147,26,.5)');
    // gradient.addColorStop(.425, 'rgba(255,193,119,0)');

    const chart = () => {
        setChartData({
            labels: [1,1,2,2,3,3,4,5,6,6,8,8,9,10,11,13,13,13,13,17,18,19,19,20,21,22,22,23,24,24,26,27,27,27,30,31,31,32,32,32,32,33,33,35,35,36,36,36,37,38,38,39,41,41,42,43,43,43,45,46,46,47,48,48,49,49,50,52,53,54,54,55,55,56,56,56,56,57,57,57,57,57,59,59,59,60,63,63,64,64,65,66,66,67,67,67,68,68,68,70,70,71,72,72,72,73,74,75,76,76,77,78,78,79,80,80,80,82,82,82,83,85,85,86,86,88,88,88,90,90,92,92,92,92,94,94,94,95,96,96,96,97,97,98,98,98,98,99,99,100
            ],
            datasets: [
                {
                    label: '$',
                    data: [1,1,2,2,3,3,4,5,6,6,8,8,9,10,11,13,13,13,13,17,18,19,19,20,21,22,22,23,24,24,26,27,27,27,30,31,31,32,32,32,32,33,33,35,35,36,36,36,37,38,38,39,41,41,42,43,43,43,45,46,46,47,48,48,49,49,50,52,53,54,54,55,55,56,56,56,56,57,57,57,57,57,59,59,59,60,63,63,64,64,65,66,66,67,67,67,68,68,68,70,70,71,72,72,72,73,74,75,76,76,77,78,78,79,80,80,80,82,82,82,83,85,85,86,86,88,88,88,90,90,92,92,92,92,94,94,94,95,96,96,96,97,97,98,98,98,98,99,99,100],
                    backgroundColor: [
                        'rgba(9, 161, 255, 0.438)'
                    ],
                    borderWidth: 4,
                    borderJoinStyle: 'round',
                    borderCapStyle: 'round',
                    borderWidth: 3,
                    pointRadius: 0,
                    pointHitRadius: 10,
                    lineTension: .2,
                }
            ]
        })
    }

    useEffect(() => {
        chart()
    }, [])

    return (
        <div id={props.className} className="smallBox">
            <div className="smallBox_info">
                <img className="smallBox-icon" src={btc} alt={props.className}/>
                <h2>{props.className}</h2>
            </div>

            <div className="smallBox_numbers">
                <h2 className="smallBox-price">$ 2,214.12</h2>
                <h3 className="smallBox-roc">+2.02%</h3>
            </div>

            <div className="smallBox_graph">
                <Line data={chartData} options={{
                    responsive: true,
                    maintainAspectRatio: false,
                    title: {text: 'ThickBoyz', display: false},
                    legend: {display: false},
                    layout: {
                        padding: {
                          left: 0,
                          right: 0,
                          top: 0,
                          bottom: 0
                        }
                      },
                      scales: {
                        xAxes: [{
                          display: false,
                          gridLines: {}
                        }],
                        yAxes: [{
                          display: false,
                          gridLines: {}
                        }]
                      },
                      tooltips: {
                        callbacks: {
                          //This removes the tooltip title
                          title: function() {}
                       },
                        //this removes legend color
                        displayColors: false,
                        yPadding: 10,
                        xPadding: 10,
                        position: 'nearest',
                        caretSize: 10,
                        bodyFontSize: 15,
                        bodyFontColor: '#303030' 
                      }
                    }}/>  
            </div>
        </div>
    )
}

export default SmallBox
