import React, { useEffect, useRef, useState } from 'react';
import {Line} from 'react-chartjs-2';
import './LargeBox.css';

function LargeBox() {
    const [chartData, setChartData] = useState({})

    const chart = () => {
        setChartData({
            labels: ['monday', 'tuesday', 'wednesday', 'thursday', 'friday'],
            datasets: [
                {
                    label: 'level of thickness',
                    data: [35, 23, 15, 54, 90],
                    backgroundColor: [
                        'rgba(75, 192, 192, 0.6)'
                    ],
                    borderWidth: 4
                }
            ]
        })
    }

    useEffect(() => {
        chart()
    }, [])
    return (
        <div className="largeBox">
            <Line data={chartData} options={{
                responsive: true,
                title: {text: 'ThickBoyz', display: true},
                scales: {
                    yAxes: [
                        {
                            ticks: {
                                autoSkip: true,
                                maxTicksLimit: 10,
                                beginAtZero: true
                            },
                            gridLines: {
                                display: false
                            }
                        }
                    ],
                    xAxes: [
                        {
                            gridLines: {
                                display: false
                            }
                        }
                    ]
                }
                }}/>   
        </div>
    )
}
export default LargeBox
