    new Chart(myChartRef, {
        type: "line",
        data: {
            //Bring in data
            labels: ["Jan", "Feb", "March"],
            datasets: [
                {
                    label: "Sales",
                    data: [86, 67, 91],
                }
            ]
        },
        options: {
            //Customize chart options
        }
    });

    return (
        <div className="largeBox">
                <canvas id="myChart" ref={chartRef}/>        
                </div>
    )
}

