import React, { useEffect, useRef, useState } from 'react';
import {Line} from 'react-chartjs-2';
import './LargeBox.css';

function LargeBox() {
    const [chartData, setChartData] = useState({})

    const chart = () => {
        setChartData({
            labels: ['monday', 'tuesday', 'wednesday', 'thursday', 'friday'],
            datasets: [
                {
                    label: 'level of thickness',
                    data: [35, 23, 15, 54, 90],
                    backgroundColor: [
                        'rgba(75, 192, 192, 0.6)'
                    ],
                    borderWidth: 4
                }
            ]
        })
    }

    useEffect(() => {
        chart()
    }, [])
    return (
        <div className="largeBox">
            <div className="largeBox_info">
                <select id="options">
                    <h2><option value="btc">
                        Bitcoin
                    </option></h2>
                    <option value="xzc">
                        ZCoin
                    </option>
                    <option value="trc">
                        Terracoin
                    </option>
                    <option value="etc">
                        Ethereum
                    </option>
                </select>
                <h2>Chart</h2>
            </div>

            <div className="largeBox_selectors">
                <ul>
                    <li>Hour</li>
                    <li>Day</li>
                    <li>Week</li>
                    <li>Month</li>
                    <li>Year</li>
                </ul>
            </div>

            <div className="largeBox_graph">
                <Line data={chartData} options={{
                    responsive: true,
                    title: {text: 'ThickBoyz', display: true},
                    scales: {
                        yAxes: [
                            {
                                ticks: {
                                    autoSkip: true,
                                    maxTicksLimit: 10,
                                    beginAtZero: true
                                },
                                gridLines: {
                                    display: false
                                }
                            }
                        ],
                        xAxes: [
                            {
                                gridLines: {
                                    display: false
                                }
                            }
                        ]
                    }
                    }}/>  
            </div> 
        </div>
    )
}
export default LargeBox
