import React, { useEffect, useRef, useState } from 'react';
import {Line} from 'react-chartjs-2';
import btc from './images/BC_Logo_.png';
import './SmallBox.css';

function SmallBox(props) {
    const [chartData, setChartData] = useState({})

    const chart = () => {
        setChartData({
            labels: ['monday', 'tuesday', 'wednesday', 'thursday', 'friday'],
            datasets: [
                {
                    label: 'level of thickness',
                    data: [35, 23, 15, 54, 90],
                    backgroundColor: [
                        'rgba(75, 192, 192, 0.6)'
                    ],
                    borderWidth: 4
                }
            ]
        })
    }

    useEffect(() => {
        chart()
    }, [])

    return (
        <div id={props.className} className="smallBox">
            <div className="smallBox_info">
                <img className="smallBox-icon" src={btc} alt={props.className}/>
                <h2>{props.className}</h2>
            </div>

            <div className="smallBox_numbers">
                <h2 className="smallBox-price">$ 2,214.12</h2>
                <h3 className="smallBox-roc">+2.02%</h3>
            </div>

            <div className="smallBox_graph">
                <Line data={chartData} options={{
                    responsive: true,
                    title: {text: 'ThickBoyz', display: true},
                    scales: {
                        yAxes: [
                            {
                                ticks: {
                                    autoSkip: true,
                                    maxTicksLimit: 10,
                                    beginAtZero: true
                                },
                                gridLines: {
                                    display: false
                                }
                            }
                        ],
                        xAxes: [
                            {
                                gridLines: {
                                    display: false
                                }
                            }
                        ]
                    }
                    }}/>  
            </div>
        </div>
    )
}

export default SmallBox
