import React, {useState, useEffect} from 'react';
import Header from './Header';
import SideBar from './Sidebar';
import SmallBox from './SmallBox';
import LongBox from './LongBox';
import HorzBox from './HorzBox';
import LargeBox from './LargeBox';
import AssetsBox from './AssetsBox';
import './App.css';

function App() {
  const [smallData, setSmallData] = useState({})

  useEffect(() => {
    fetch(`https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=100&page=1&sparkline=false`)
    .then(response => response.json())
    .then(data => {
      setSmallData(data)
    })
  })

  return (
    <div className="app">
      {/* Header */}
      <Header />

      {/* Left SideBar */}
      <SideBar />

      {/* SmallBox - Balance */}
      <SmallBox className="balance" />

      {/* SmallBox - Bitcoin */}
      <SmallBox className="bitcoin" 
        id={smallData[0].id}
        symbol={smallData[0].symbol}
        currentPrice={smallData[0].current_price}
        percentChange={smallData[0].price_change_percent}
        color= "#f7931a"
        title={smallData[0].name}
        image={smallData[0].image}
      />

      {/* SmallBox - Terracoin */}
      <SmallBox className="ethereum" 
        id={smallData[1].id}
        symbol={smallData[1].symbol}
        currentPrice={smallData[1].current_price}
        percentChange={smallData[1].price_change_percent}
        color= "#f7931a"
        title={smallData[1].name}
        image={smallData[1].image} 
      />

      {/* SmallBox - ZCoin */}
      <SmallBox className="ripple" 
        id={smallData[2].id}
        symbol={smallData[2].symbol}
        currentPrice={smallData[2].current_price}
        percentChange={smallData[2].price_change_percent}
        color= "#f7931a"
        title={smallData[2].name}
        image={smallData[2].image} 
      />

      {/* SmallBox - Ethereum */}
      <SmallBox className="tether" 
        id={smallData[3].id}
        symbol={smallData[3].symbol}
        currentPrice={smallData[3].current_price}
        percentChange={smallData[3].price_change_percent}
        color= "#62688f"
        title={smallData[3].name}
        image={smallData[3].image} 
      />

      {/* LongBox - Recent Operations */}
      <LongBox />

      {/* HorizontalBox - Buy/Sell Coins */}
      <HorzBox />

      {/* LargeBox - Graph (BTC) */}
      <LargeBox />

      {/* AssetBox - Assets*/}
      <AssetsBox />
    </div>
  );
}

export default App;
