import React, { useEffect, useRef, useState } from 'react';
import {Line} from 'react-chartjs-2';
import btc from './images/BC_Logo_.png';
import './SmallBox.css';

function SmallBox(props) {
    const [chartData, setChartData] = useState({})

    // let gradient = chart.createLinearGradient(0, 0, 0, 400);

    // gradient.addColorStop(0, 'rgba(247,147,26,.5)');
    // gradient.addColorStop(.425, 'rgba(255,193,119,0)');

    const chart = () => {
        setChartData({
            labels: ['monday', 'tuesday', 'wednesday', 'thursday', 'friday'],
            datasets: [
                {
                    label: 'level of thickness',
                    data: [35, 23, 15, 54, 90],
                    backgroundColor: [
                        'rgb(9,159,255)'
                    ],
                    borderWidth: 4,
                    borderJoinStyle: 'round',
                    borderCapStyle: 'round',
                    borderWidth: 3,
                    pointRadius: 0,
                    pointHitRadius: 10,
                    lineTension: .2,
                }
            ]
        })
    }

    useEffect(() => {
        chart()
    }, [])

    return (
        <div id={props.className} className="smallBox">
            <div className="smallBox_info">
                <img className="smallBox-icon" src={btc} alt={props.className}/>
                <h2>{props.className}</h2>
            </div>

            <div className="smallBox_numbers">
                <h2 className="smallBox-price">$ 2,214.12</h2>
                <h3 className="smallBox-roc">+2.02%</h3>
            </div>

            <div className="smallBox_graph">
                <Line data={chartData} options={{
                    responsive: true,
                    title: {text: 'ThickBoyz', display: false},
                    legend: {display: false},
                    layout: {
                        padding: {
                          left: 0,
                          right: 0,
                          top: 0,
                          bottom: 0
                        }
                      },
                      scales: {
                        xAxes: [{
                          display: false,
                          gridLines: {}
                        }],
                        yAxes: [{
                          display: false,
                          gridLines: {}
                        }]
                      },
                      tooltips: {
                        callbacks: {
                          //This removes the tooltip title
                          title: function() {}
                       },
                        //this removes legend color
                        displayColors: false,
                        yPadding: 10,
                        xPadding: 10,
                        position: 'nearest',
                        caretSize: 10,
                        backgroundColor: 'rgba(255,255,255,.9)',
                        bodyFontSize: 15,
                        bodyFontColor: '#303030' 
                      }
                    }}/>  
            </div>
        </div>
    )
}

export default SmallBox
