import React, {useState} from 'react'
import './HorzBox.css';
import SwapHorizIcon from '@material-ui/icons/SwapHoriz';

function HorzBox(props) {
    const [inputNum, setInputNum] = useState("1")
    const [selectedCoin, setSelectedCoin] = useState("btc")
    const [price, setPrice] = useState(props.bitcoinPrice)
    const [buy, setBuy] = useState(false);

    function unitsChange(e){
        setInputNum(e.target.value)
    }

    function currentCoin(e){
        if(e.target.options.selectedIndex === 0){
            setSelectedCoin('bitcoin');
            setPrice(props.bitcoinPrice)

        } else if(e.target.options.selectedIndex === 1){
            setSelectedCoin('ethereum');
            setPrice(props.ethereumPrice)
        } else if(e.target.options.selectedIndex === 2){
            setSelectedCoin('stellar');
            setPrice(props.stellarPrice)
        } else if(e.target.options.selectedIndex === 3){
            setSelectedCoin('tether');
            setPrice(props.tetherPrice)
        }
    }

    return (
        <div className="horzBox">
            <form>
                <div id="buyOrSell">
                    <p value="buy" className="active buy">Buy</p>
                    <p value="sell" className="sell">Sell</p>
                </div>
                <div id="selections">
                {buy ? (
                <div>
                <p value="current">Balance</p>
                <SwapHorizIcon />
                <select id="options" onChange={currentCoin}>
                    <option value={0}>
                        Bitcoin
                    </option>
                    <option value={1}>
                        Ethereum
                    </option>
                    <option value={2}>
                        Stellar
                    </option>
                    <option value={3}>
                        Tether
                    </option>
                </select>
                </div>
                ) : (
                <div>
                <select id="options" onChange={currentCoin}>
                    <option value={0}>
                        Bitcoin
                    </option>
                    <option value={1}>
                        Ethereum
                    </option>
                    <option value={2}>
                        Stellar
                    </option>
                    <option value={3}>
                        Tether
                    </option>
                </select>
                <p value="current">Balance</p>
                <SwapHorizIcon />
                </div>
                )}
                </div>
                <div id="horzBox-info">
                    <p>Today's Price: <br/> <span>{price}</span></p>
                    <input type="number" placeholder="1" onChange={unitsChange}/>
                    <p> Total Price: <br/> <span>{price * inputNum}</span></p>
                </div>
                <button id="confirm">Confirm</button>
            </form>


        </div>
    )
}

export default HorzBox
