import React, {useState, useEffect} from 'react';
import Header from './Header';
import SideBar from './Sidebar';
import SmallBox from './SmallBox';
import LongBox from './LongBox';
import HorzBox from './HorzBox';
import LargeBox from './LargeBox';
import AssetsBox from './AssetsBox';
import './App.css';

function App() {
  const [smallData, setSmallData] = useState({})

  function getAPI(){
    fetch(`https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=100&page=1&sparkline=false
    `)
    .then(response => response.json())
    .then(data => setSmallData({data})); 

  }

  useEffect( () => {
    getAPI();
    console.log(smallData)
  }, []);




  return (
    <div className="app">
      {/* Header */}
      <Header />

      {/* Left SideBar */}
      <SideBar />

      {/* SmallBox - Balance */}
      <SmallBox className="balance"/>

      {/* SmallBox - Bitcoin */}
      <SmallBox className="bitcoin" coin={smallData}/>

      {/* SmallBox - Terracoin */}
      <SmallBox className="ethereum" coin={smallData}/>

      {/* SmallBox - ZCoin */}
      <SmallBox className="ripple" coin={smallData}/>

      {/* SmallBox - Ethereum */}
      <SmallBox className="tether" coin={smallData}/>

      {/* LongBox - Recent Operations */}
      <LongBox />

      {/* HorizontalBox - Buy/Sell Coins */}
      <HorzBox />

      {/* LargeBox - Graph (BTC) */}
      <LargeBox />

      {/* AssetBox - Assets*/}
      <AssetsBox />
    </div>
  );
}

export default App;
